import React, { useState } from "react";
import { IoIosArrowDown } from "react-icons/io";

const CollapseBook = () => {
  const [collapseState, setCollapseState] = useState({
    item1: false,
    item2: false,
    item3: false,
    item4: false,
  });

  const toggleCollapse = (item) => {
    setCollapseState((prevState) => ({
      ...prevState,
      [item]: !prevState[item],
    }));
  };

  return (
    <ul className="bloc_react">
      <li>
        <div className="divider"></div>
        <div className="mini_bloc">
          <h5>What is Bookmark?</h5>
          <div>
            <IoIosArrowDown
              className="arrow_bottom"
              onClick={() => toggleCollapse("item1")}
            />
          </div>
        </div>
        {collapseState.item1 && (
          <p className="text_bloc">
            {/* Contenu du collapse pour 'What is Bookmark?' */}
          </p>
        )}
        <div className="divider"></div>
      </li>
      <li>
        <div className="divider"></div>
        <div className="mini_bloc">
          <h5>How can I request a new browser?</h5>
          <div>
            <IoIosArrowDown
              className="arrow_bottom"
              onClick={() => toggleCollapse("item2")}
            />
          </div>
        </div>
        {collapseState.item2 && (
          <p className="text_bloc">
            {/* Contenu du collapse pour 'How can I request a new browser?' */}
          </p>
        )}
        <div className="divider"></div>
      </li>
      <li>
        <div className="divider"></div>
        <div className="mini_bloc">
          <h5>Is there a mobile app?</h5>
          <div>
            <IoIosArrowDown
              className="arrow_bottom"
              onClick={() => toggleCollapse("item3")}
            />
          </div>
        </div>
        {collapseState.item3 && (
          <p className="text_bloc">
            {/* Contenu du collapse pour 'Is there a mobile app?' */}
          </p>
        )}
        <div className="divider"></div>
      </li>
      <li>
        <div className="divider"></div>
        <div className="mini_bloc">
          <h5>What about other Chromium browsers?</h5>
          <div>
            <IoIosArrowDown
              className="arrow_bottom"
              onClick={() => toggleCollapse("item4")}
            />
          </div>
        </div>
        {collapseState.item4 && (
          <p className="text_bloc">
            {/* Contenu du collapse pour 'What about other Chromium browsers?' */}
          </p>
        )}
        <div className="divider"></div>
      </li>
    </ul>
  );
};

export default CollapseBook;
